use color_eyre::eyre::Result;
use std::fs::OpenOptions;
use std::io::Write;

#[derive(Debug)]
pub struct Output {
	pub main_line_str: String,
	pub spy_line_str: String,
	pub additional_line_str: String,
}

//? potentially, could store last modified for every value, subsequently that all of them are recent enough when called from the main loop.
impl Output {
	pub fn out(&self) -> Result<()> {
		self.write_to_pipe("/tmp/btc_line_main", &self.main_line_str)?;
		self.write_to_pipe("/tmp/btc_line_spy", &self.spy_line_str)?;
		self.write_to_pipe("/tmp/btc_line_additional", &self.additional_line_str)?;
		Ok(())
	}

	fn write_to_pipe(&self, pipe_path: &str, content: &str) -> Result<()> {
		if let Ok(mut file) = OpenOptions::new().write(true).open(pipe_path) {
			let _ = writeln!(file, "{}", content);
		}
		Ok(())
	}

	pub fn new() -> Self {
		Self {
			main_line_str: "".to_string(),
			spy_line_str: "".to_string(),
			additional_line_str: "".to_string(),
		}
	}
}
