use leptos::{ev, html::*, prelude::*};
use leptos_meta::{MetaTags, Stylesheet, StylesheetProps, Title, TitleProps, provide_meta_context};
use leptos_router::{
    components::{A, AProps, Router, Route, Routes},
    path,
    SsrMode,
};

use crate::dashboards::DashboardsView;

pub fn shell(options: LeptosOptions) -> impl IntoView {
	view! {
		<!DOCTYPE html> 
		<html lang="en">
			<head>
				<meta charset="utf-8" />
				<meta name="viewport" content="width=device-width, initial-scale=1" />
				<AutoReload options=options.clone() />
				<HydrationScripts options islands=true />
				<MetaTags />
				// all embedded plotly plots assume this is in scope
				<script src="https://cdn.plot.ly/plotly-3.0.1.min.js"></script>
			</head>
			<body>
				<App />
			</body>
		</html>
	}
}

#[component]
pub fn App() -> impl IntoView {
	provide_meta_context();
	//TODO: with strum and derive_more, gen a nav bar at the top for all routes (ref: `nested` example in leptos-routable)
	(
		Stylesheet(StylesheetProps {
			id: Some("leptos".to_owned()),
			href: format!("/pkg/{}.css", env!("CARGO_PKG_NAME")),
		}),
		Title(TitleProps {
			formatter: None,
			text: Some("My Site".into()),
		}),
		main().class("min-h-screen").child(view! { 
			<Router>
				<Routes fallback=NotFoundView>
					<Route path=path!("/") view=HomeView ssr=SsrMode::Async/>
					<Route path=path!("/dashboards") view=DashboardsView ssr=SsrMode::Async/>
				</Routes>
			</Router>
		}),
	)
}


